function articleProgress(){const e=document.querySelector(".article-progress"),o=e.querySelector(".article-progress-inner");window.addEventListener("scroll",()=>{const e=window.scrollY||document.documentElement.scrollTop,r=document.documentElement.scrollHeight-window.innerHeight,t=e/r*100,n=t.toFixed(2);o.style.width=`${n}%`})}articleProgress();